    from PROCESS_LOG_DATA
    where
        INSTANCE_ID = P_INSTANCE_ID and INSTANCE_CREATED_AT = P_CREATED_AT and SEGMENT_ID = P_SEGMENT_ID;

    if R_START is null then
        R_START := 0;
    end if;

    return int4range(R_START - P_DATA_LEN, R_START);
end;
$$ language plpgsql;

